mapper.select:
select

from eb_notice
where title = #{title,jdbcType=VARCHAR}

  and id <> #{id}

select
, case when STR_TO_DATE(publish_time, '%Y-%m-%d')> DATE_FORMAT(CURDATE(),'%Y-%m-%d') then '2' when STR_TO_DATE(over_time, '%Y-%m-%d')> DATE_FORMAT(CURDATE(),'%Y-%m-%d') and STR_TO_DATE(publish_time, '%Y-%m-%d')<= DATE_FORMAT(CURDATE(),'%Y-%m-%d') then '1' when STR_TO_DATE(over_time, '%Y-%m-%d')<=DATE_FORMAT(CURDATE(),'%Y-%m-%d') then '3' else '0' end as noticeStatus from eb_notice
order by create_time DESC limit ${start},${rows}

mapper.update:
update eb_notice
set is_del=1
where id = #{id,jdbcType=BIGINT}
